import { useState } from 'react';
import { useMutation } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import BackButton from '@/components/BackButton';
import { usePageTracking, useActivityLogger } from '@/hooks/useActivityLogger';
import { 
  Mail, 
  Phone, 
  MapPin, 
  Clock, 
  Send,
  MessageSquare,
  CheckCircle,
  Facebook,
  Twitter,
  Instagram,
  Linkedin
} from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';

interface ContactFormData {
  name: string;
  email: string;
  phone: string;
  subject: string;
  message: string;
}

export default function Contact() {
  usePageTracking('Contact');
  const { logUserAction } = useActivityLogger();
  const [formData, setFormData] = useState<ContactFormData>({
    name: '',
    email: '',
    phone: '',
    subject: '',
    message: '',
  });

  const { toast } = useToast();

  const mutation = useMutation({
    mutationFn: (data: ContactFormData) => 
      fetch('/api/contact', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      }).then(res => {
        if (!res.ok) throw new Error('Failed to send message');
        return res.json();
      }),
    onSuccess: () => {
      toast({
        title: "Message sent!",
        description: "We'll get back to you within 24 hours.",
      });
      setFormData({
        name: '',
        email: '',
        phone: '',
        subject: '',
        message: '',
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to send message. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    logUserAction('contact_form_submit', 'Contact', { subject: formData.subject });
    mutation.mutate(formData);
  };

  const handleChange = (field: keyof ContactFormData, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const contactInfo = [
    {
      icon: Mail,
      title: 'Email Us',
      value: 'ironledgermedmap@gmail.com',
      description: 'We respond within 24 hours',
      href: 'mailto:ironledgermedmap@gmail.com'
    },
    {
      icon: Phone,
      title: 'Call Us',
      value: '+27 11 456 7890',
      description: 'Mon-Fri, 8AM-6PM',
      href: 'tel:+27114567890'
    },
    {
      icon: MapPin,
      title: 'Visit Us',
      value: 'Sandton, Johannesburg, South Africa',
      description: 'By appointment only',
      href: '#'
    },
  ];

  const socialLinks = [
    { icon: Facebook, name: 'Facebook', href: 'https://facebook.com/ironledgermedmap' },
    { icon: Twitter, name: 'Twitter', href: 'https://twitter.com/ironledgermedmap' },
    { icon: Instagram, name: 'Instagram', href: 'https://instagram.com/ironledgermedmap' },
    { icon: Linkedin, name: 'LinkedIn', href: 'https://linkedin.com/company/ironledgermedmap' },
  ];

  return (
    <div className="min-h-screen bg-background py-12" data-testid="page-contact">
